import {useState } from "react";
import "./App.css";

function App() {
  const [inp, setInp] = useState(true);
  const [users, setUsers] = useState({});
  const [loading, setLoading] = useState(false);

  const getInp = () => {
    setInp(false);
    fetchData();
  }

  const fetchData = () => {
    setLoading(true);
    fetch("https://reqres.in/api/users?page=1")
      .then((response) => response.json())
      .then((data) => {
        setTimeout(() => {
          setUsers(data.data);
          setLoading(false);
        }, 500);
      });
  };

  return (
    <div class="main">
      {inp ? (
        <header class="head">
          <h1><strong>INFORMATION OF LGM USERS</strong></h1>
          <button class="button" onClick={getInp}>Click</button>
        </header>
      ) : (
        <>
          {loading ? (
            <div class="loader"></div>
          ) : (
            <div class="container">
              {users.map((item, key) => {
                return <div class="card_item" key={key}>
                  <div class="card_inner">
                    <img src={item.avatar} alt="Avatar" />
                      <div class="username">Name : {item.first_name} {item.last_name}</div>
                      <div class="email">Email : {item.email}</div>
                    </div>
                  </div>
                })}
            </div>
          )}
        </>
      )}
    </div>
  );
}

export default App;
